@app.callback(
    Output('content1', 'figure'),
    Input('Select Data Chart', 'value'))

def output_single1(value):
    net_df = pd.DataFrame()
    net_df['Prod./Merch. Net Position'] = cot_futures['Prod_Merch_Long'] - cot_futures['Prod_Merch_Short']
    net_df['Swaps Net Position'] = cot_futures['Swaps_Long'] - cot_futures['Swaps_Short']
    net_df['Managed M. Net Position'] = cot_futures['Mana_M_Long'] - cot_futures['Mana_M_Short']


    fig = go.Figure()

    for _ in net_df.columns:
        fig.add_trace(go.Scatter(name = f'{_}', x=cot_futures.index ,y=net_df[f'{_}']))

    for _ in cot_futures.columns:
        fig.add_trace(go.Bar(name = f'{_}', x=cot_futures.index, y=cot_futures[f'{_}']))

    fig.update_layout(
    autosize=True,
    font_color = 'black',
    height = 1000,
    template='plotly',
    title={
    'text': 'Crude Oil Futures Report'})

    return fig

